import { PrismaClient } from '@prisma/client'

const prismaClientSingleton = () => {
	return new PrismaClient()
}

declare const globalThis: {
	prismaGlobal: ReturnType<typeof prismaClientSingleton>
} & typeof global

const prisma = globalThis.prismaGlobal ?? prismaClientSingleton()

export default prisma

if (process.env.NODE_ENV !== 'production') globalThis.prismaGlobal = prisma

import { createEnv } from '@t3-oss/env-nextjs'
import { z } from 'zod'

export const env = createEnv({
	server: { DATABASE_URL: z.string().url() },
	client: { NEXT_PUBLIC_SITE_URL: z.string().url() },
	runtimeEnv: {
		DATABASE_URL: process.env.DATABASE_URL,
		NEXT_PUBLIC_SITE_URL: process.env.NEXT_PUBLIC_SITE_URL,
	},
})

datasource db {
  provider = "postgresql"
  url      = env("DATABASE_URL")
}

generator client {
  provider = "prisma-client-js"
}

enum TodoStatus {
    NEW
    IN_PROGRESS
    COMPLETED
}

model Todo {
    id         String   @id @default(cuid())
    created_at DateTime @default(now())
    updated_at DateTime @updatedAt

    title  String
    desc   String?
    status TodoStatus @default(NEW)
}

import { readFileSync, writeFileSync } from 'fs'
import { join } from 'path'

export function main() {
	try {
		// Read the current package.json
		const packageJsonPath = join(process.cwd(), 'package.json')
		const packageJson = JSON.parse(readFileSync(packageJsonPath, 'utf-8'))

		// Add or update the prisma configuration
		packageJson.prisma = { schema: './prisma/schema' }

		// Add or update the postinstall script
		packageJson.scripts = {
			...packageJson.scripts,
			postinstall: 'npx prisma generate',
		}

		// Write the updated package.json back to the file
		writeFileSync(packageJsonPath, JSON.stringify(packageJson, null, 2))

		console.log('Successfully added Prisma configuration and postinstall script to package.json')
	} catch (error) {
		console.error('Error updating package.json:', error)
		process.exit(1)
	}
}

main()
