'use client'

import { createContext, useContext, JSX } from 'react'

import { LexicalEditor } from 'lexical'

const Context = createContext<{
  activeEditor: LexicalEditor
  $updateToolbar: () => void
  blockType: string
  setBlockType: (blockType: string) => void
  showModal: (title: string, showModal: (onClose: () => void) => JSX.Element) => void
}>({
  activeEditor: {} as LexicalEditor,
  $updateToolbar: () => {},
  blockType: 'paragraph',
  setBlockType: () => {},
  showModal: () => {},
})

export function ToolbarContext({
  activeEditor,
  $updateToolbar,
  blockType,
  setBlockType,
  showModal,
  children,
}: {
  activeEditor: LexicalEditor
  $updateToolbar: () => void
  blockType: string
  setBlockType: (blockType: string) => void
  showModal: (title: string, showModal: (onClose: () => void) => JSX.Element) => void
  children: React.ReactNode
}) {
  return (
    <Context.Provider
      value={{ activeEditor, $updateToolbar, blockType, setBlockType, showModal }}
    >
      {children}
    </Context.Provider>
  )
}

export function useToolbarContext() {
  return useContext(Context)
}

import { useCallback, useMemo, useState, JSX } from 'react'
import * as React from 'react'

import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog'

export function useEditorModal(): [
  JSX.Element | null,
  (title: string, showModal: (onClose: () => void) => JSX.Element) => void,
] {
  const [modalContent, setModalContent] = useState<null | {
    closeOnClickOutside: boolean
    content: JSX.Element
    title: string
  }>(null)

  const onClose = useCallback(() => {
    setModalContent(null)
  }, [])

  const modal = useMemo(() => {
    if (modalContent === null) {
      return null
    }
    const { title, content } = modalContent
    return (
      <Dialog open={true} onOpenChange={onClose}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>{title}</DialogTitle>
          </DialogHeader>
          {content}
        </DialogContent>
      </Dialog>
    )
  }, [modalContent, onClose])

  const showModal = useCallback(
    (
      title: string,
      getContent: (onClose: () => void) => JSX.Element,
      closeOnClickOutside = false
    ) => {
      setModalContent({
        closeOnClickOutside,
        content: getContent(onClose),
        title,
      })
    },
    [onClose]
  )

  return [modal, showModal]
}

import { useEffect } from 'react'

import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext'
import { $getSelection, BaseSelection, COMMAND_PRIORITY_CRITICAL, SELECTION_CHANGE_COMMAND } from 'lexical'

import { useToolbarContext } from '@/components/ui/editor/context/toolbar-context'

export function useUpdateToolbarHandler(callback: (selection: BaseSelection) => void) {
	const [editor] = useLexicalComposerContext()
	const { activeEditor } = useToolbarContext()

	useEffect(() => {
		return activeEditor.registerCommand(
			SELECTION_CHANGE_COMMAND,
			() => {
				const selection = $getSelection()
				if (selection) {
					callback(selection)
				}
				return false
			},
			COMMAND_PRIORITY_CRITICAL
		)
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [editor, callback])

	useEffect(() => {
		activeEditor.getEditorState().read(() => {
			const selection = $getSelection()
			if (selection) {
				callback(selection)
			}
		})
	}, [activeEditor, callback])
}

