'use client'

import { useState } from 'react'

import { $isTableSelection } from '@lexical/table'
import { $isRangeSelection, BaseSelection, FORMAT_TEXT_COMMAND, TextFormatType } from 'lexical'
import { BoldIcon, CodeIcon, ItalicIcon, StrikethroughIcon, UnderlineIcon } from 'lucide-react'

import { Toggle } from '@/components/ui/toggle'

import { useToolbarContext } from '@/components/ui/editor/context/toolbar-context'
import { useUpdateToolbarHandler } from '@/components/ui/editor/editor-hooks/use-update-toolbar'

const Icons: Partial<Record<TextFormatType, React.ElementType>> = {
	bold: BoldIcon,
	italic: ItalicIcon,
	underline: UnderlineIcon,
	strikethrough: StrikethroughIcon,
	code: CodeIcon,
} as const

export function FontFormatToolbarPlugin({ format }: { format: Omit<TextFormatType, 'highlight' | 'subscript' | 'superscript'> }) {
	const { activeEditor } = useToolbarContext()
	const [isSelected, setIsSelected] = useState<boolean>(false)

	const $updateToolbar = (selection: BaseSelection) => {
		if ($isRangeSelection(selection) || $isTableSelection(selection)) {
			setIsSelected(selection.hasFormat(format as TextFormatType))
		}
	}

	useUpdateToolbarHandler($updateToolbar)

	const Icon = Icons[format as TextFormatType] as React.ElementType

	return (
		<Toggle
			aria-label='Toggle bold'
			variant='outline'
			size='sm'
			defaultPressed={isSelected}
			pressed={isSelected}
			onPressedChange={setIsSelected}
			onClick={() => {
				activeEditor.dispatchCommand(FORMAT_TEXT_COMMAND, format as TextFormatType)
			}}>
			<Icon className='h-4 w-4' />
		</Toggle>
	)
}

'use client'

import { useCallback, useState } from 'react'

import { $getSelectionStyleValueForProperty, $patchStyleText } from '@lexical/selection'
import { $getSelection, $isRangeSelection, BaseSelection } from 'lexical'
import { Minus, Plus } from 'lucide-react'

import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'

import { useToolbarContext } from '@/components/ui/editor/context/toolbar-context'
import { useUpdateToolbarHandler } from '@/components/ui/editor/editor-hooks/use-update-toolbar'

const DEFAULT_FONT_SIZE = 16
const MIN_FONT_SIZE = 1
const MAX_FONT_SIZE = 72

export function FontSizeToolbarPlugin() {
	const style = 'font-size'
	const [fontSize, setFontSize] = useState(DEFAULT_FONT_SIZE)

	const { activeEditor } = useToolbarContext()

	const $updateToolbar = (selection: BaseSelection) => {
		if ($isRangeSelection(selection)) {
			const value = $getSelectionStyleValueForProperty(selection, 'font-size', `${DEFAULT_FONT_SIZE}px`)
			setFontSize(parseInt(value) || DEFAULT_FONT_SIZE)
		}
	}

	useUpdateToolbarHandler($updateToolbar)

	const updateFontSize = useCallback(
		(newSize: number) => {
			const size = Math.min(Math.max(newSize, MIN_FONT_SIZE), MAX_FONT_SIZE)
			activeEditor.update(() => {
				const selection = $getSelection()
				if (selection !== null) {
					$patchStyleText(selection, {
						[style]: `${size}px`,
					})
				}
			})
			setFontSize(size)
		},
		[activeEditor, style]
	)

	return (
		<div className='flex items-center gap-1'>
			<div className='flex items-center gap-1'>
				<Button
					variant='outline'
					size='icon'
					className='h-8 w-8'
					onClick={() => updateFontSize(fontSize - 1)}
					disabled={fontSize <= MIN_FONT_SIZE}>
					<Minus className='size-3' />
				</Button>
				<Input
					value={fontSize}
					onChange={(e) => updateFontSize(parseInt(e.target.value) || DEFAULT_FONT_SIZE)}
					className='h-8 w-12 text-center'
					min={MIN_FONT_SIZE}
					max={MAX_FONT_SIZE}
				/>
				<Button
					variant='outline'
					size='icon'
					className='h-8 w-8'
					onClick={() => updateFontSize(fontSize + 1)}
					disabled={fontSize >= MAX_FONT_SIZE}>
					<Plus className='size-3' />
				</Button>
			</div>
		</div>
	)
}

'use client'

import { useEffect, useState } from 'react'

import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext'
import { mergeRegister } from '@lexical/utils'
import { CAN_REDO_COMMAND, CAN_UNDO_COMMAND, COMMAND_PRIORITY_CRITICAL, REDO_COMMAND, UNDO_COMMAND } from 'lexical'
import { RedoIcon, UndoIcon } from 'lucide-react'

import { Button } from '@/components/ui/button'

import { useToolbarContext } from '@/components/ui/editor/context/toolbar-context'

const IS_APPLE = true

export function HistoryToolbarPlugin() {
	const [editor] = useLexicalComposerContext()
	const { activeEditor, $updateToolbar } = useToolbarContext()
	const [isEditable, setIsEditable] = useState(editor.isEditable())
	const [canUndo, setCanUndo] = useState(false)
	const [canRedo, setCanRedo] = useState(false)

	useEffect(() => {
		return mergeRegister(
			editor.registerEditableListener((editable) => {
				setIsEditable(editable)
			}),
			activeEditor.registerUpdateListener(({ editorState }) => {
				editorState.read(() => {
					$updateToolbar()
				})
			}),
			activeEditor.registerCommand<boolean>(
				CAN_UNDO_COMMAND,
				(payload) => {
					setCanUndo(payload)
					return false
				},
				COMMAND_PRIORITY_CRITICAL
			),
			activeEditor.registerCommand<boolean>(
				CAN_REDO_COMMAND,
				(payload) => {
					setCanRedo(payload)
					return false
				},
				COMMAND_PRIORITY_CRITICAL
			)
		)
	}, [$updateToolbar, activeEditor, editor])

	return (
		<div className='flex items-center gap-1'>
			<Button
				disabled={!canUndo || !isEditable}
				onClick={() => {
					activeEditor.dispatchCommand(UNDO_COMMAND, undefined)
				}}
				title={IS_APPLE ? 'Undo (⌘Z)' : 'Undo (Ctrl+Z)'}
				type='button'
				aria-label='Undo'
				size='sm'
				className='h-8 w-8'
				variant={'outline'}>
				<UndoIcon className='size-4' />
			</Button>
			<Button
				disabled={!canRedo || !isEditable}
				onClick={() => {
					activeEditor.dispatchCommand(REDO_COMMAND, undefined)
				}}
				title={IS_APPLE ? 'Redo (⇧⌘Z)' : 'Redo (Ctrl+Y)'}
				type='button'
				aria-label='Redo'
				size='sm'
				className='h-8 w-8'
				variant={'outline'}>
				<RedoIcon className='size-4' />
			</Button>
		</div>
	)
}

'use client'

import { useState } from 'react'

import { $isTableSelection } from '@lexical/table'
import { $isRangeSelection, BaseSelection, FORMAT_TEXT_COMMAND } from 'lexical'
import { SubscriptIcon, SuperscriptIcon } from 'lucide-react'

import { ToggleGroup, ToggleGroupItem } from '@/components/ui/toggle-group'

import { useToolbarContext } from '@/components/ui/editor/context/toolbar-context'
import { useUpdateToolbarHandler } from '@/components/ui/editor/editor-hooks/use-update-toolbar'

export function SubSuperToolbarPlugin() {
	const { activeEditor } = useToolbarContext()
	const [isSubscript, setIsSubscript] = useState(false)
	const [isSuperscript, setIsSuperscript] = useState(false)

	const $updateToolbar = (selection: BaseSelection) => {
		if ($isRangeSelection(selection) || $isTableSelection(selection)) {
			setIsSubscript(selection.hasFormat('subscript'))
			setIsSuperscript(selection.hasFormat('superscript'))
		}
	}

	useUpdateToolbarHandler($updateToolbar)

	return (
		<ToggleGroup type='single' defaultValue={isSubscript ? 'subscript' : isSuperscript ? 'superscript' : ''}>
			<ToggleGroupItem
				value='subscript'
				aria-label='Toggle subscript'
				onClick={() => {
					activeEditor.dispatchCommand(FORMAT_TEXT_COMMAND, 'subscript')
				}}
				size='sm'
				variant={'outline'}>
				<SubscriptIcon className='h-4 w-4' />
			</ToggleGroupItem>
			<ToggleGroupItem
				value='superscript'
				aria-label='Toggle superscript'
				onClick={() => {
					activeEditor.dispatchCommand(FORMAT_TEXT_COMMAND, 'superscript')
				}}
				size='sm'
				variant={'outline'}>
				<SuperscriptIcon className='h-4 w-4' />
			</ToggleGroupItem>
		</ToggleGroup>
	)
}

'use client'

import { useEffect, useState } from 'react'

import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext'
import { COMMAND_PRIORITY_CRITICAL, SELECTION_CHANGE_COMMAND } from 'lexical'

import { ToolbarContext } from '@/components/ui/editor/context/toolbar-context'

import { useEditorModal } from '@/components/ui/editor/editor-hooks/use-modal'

export function ToolbarPlugin({ children }: { children: (props: { blockType: string }) => React.ReactNode }) {
	const [editor] = useLexicalComposerContext()

	const [activeEditor, setActiveEditor] = useState(editor)
	const [blockType, setBlockType] = useState<string>('paragraph')

	const [modal, showModal] = useEditorModal()

	const $updateToolbar = () => {}

	useEffect(() => {
		return activeEditor.registerCommand(
			SELECTION_CHANGE_COMMAND,
			(_payload, newEditor) => {
				setActiveEditor(newEditor)
				return false
			},
			COMMAND_PRIORITY_CRITICAL
		)
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [editor])

	return (
		<ToolbarContext
			activeEditor={activeEditor}
			$updateToolbar={$updateToolbar}
			blockType={blockType}
			setBlockType={setBlockType}
			showModal={showModal}>
			{modal}

			{children({ blockType })}
		</ToolbarContext>
	)
}

