'use server'

import prisma from '@/lib/db'
import { env } from '@/lib/env'
import { s3 } from '@/lib/s3'
import { DeleteObjectCommand, GetObjectCommand, PutObjectCommand } from '@aws-sdk/client-s3'
import { getSignedUrl } from '@aws-sdk/s3-request-presigner'
import { Prisma } from '@prisma/client'
import { revalidatePath } from 'next/cache'
import { v4 as uuidv4 } from 'uuid'

export async function uploadPublicFile(filename: string, ContentType = 'application/octet-stream') {
	const Key = uuidv4()
	const cmd = new PutObjectCommand({ Bucket: env.AWS_PUBLIC_BUCKET_NAME, Key, ContentType })
	const url = await getSignedUrl(s3, cmd, { expiresIn: 60 })

	await prisma.publicFiles.create({
		data: {
			key: Key,
			original_name: filename,
			mime_type: ContentType,
			url: `https://${env.AWS_PUBLIC_BUCKET_NAME}.s3.${env.AWS_REGION}.amazonaws.com/${Key}`,
		},
	})

	return { url }
}

export async function deletePublicFile(Key: string) {
	await s3.send(new DeleteObjectCommand({ Bucket: env.AWS_PUBLIC_BUCKET_NAME, Key }))
	await prisma.publicFiles.delete({ where: { key: Key } })
	revalidatePath('/example')
}

export async function getPrivateFiles(args?: Prisma.PrivateFilesFindManyArgs) {
	const filesFromDb = await prisma.privateFiles.findMany(args)

	const files = await Promise.all(
		filesFromDb.map(async (f) => ({
			...f,
			url: await getSignedUrl(s3, new GetObjectCommand({ Bucket: env.AWS_PRIVATE_BUCKET_NAME, Key: f.key }), { expiresIn: 60 }),
		}))
	)

	return files
}

export async function uploadPrivateFile(filename: string, ContentType = 'application/octet-stream') {
	const Key = uuidv4()
	const cmd = new PutObjectCommand({ Bucket: env.AWS_PRIVATE_BUCKET_NAME, Key, ContentType })
	const url = await getSignedUrl(s3, cmd, { expiresIn: 60 })
	await prisma.privateFiles.create({ data: { key: Key, original_name: filename, mime_type: ContentType } })
	return { url }
}

export async function deletePrivateFile(Key: string) {
	await s3.send(new DeleteObjectCommand({ Bucket: env.AWS_PRIVATE_BUCKET_NAME, Key }))
	await prisma.privateFiles.delete({ where: { key: Key } })
	revalidatePath('/example')
}

'use client'

import { uploadPrivateFile, uploadPublicFile } from '@/actions/s3'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { useRouter } from 'next/navigation'
import { useState } from 'react'

export function UploadPublicFile() {
	const router = useRouter()
	const [file, setFile] = useState<File | null>(null)

	async function handleUpload() {
		if (!file) return
		const { url } = await uploadPublicFile(file.name, file.type)
		console.log(url)
		await fetch(url, { method: 'PUT', body: file })
		router.refresh()
	}

	return (
		<div className='flex gap-2'>
			<Input className='flex-1' type='file' onChange={(e) => setFile(e.target.files?.[0] ?? null)} />
			<Button onClick={handleUpload}>Upload</Button>
		</div>
	)
}

export function UploadPrivateFile() {
	const router = useRouter()
	const [file, setFile] = useState<File | null>(null)

	async function handleUpload() {
		if (!file) return
		const { url } = await uploadPrivateFile(file.name, file.type)
		await fetch(url, { method: 'PUT', body: file })
		router.refresh()
	}

	return (
		<div className='flex gap-2'>
			<Input className='flex-1' type='file' onChange={(e) => setFile(e.target.files?.[0] ?? null)} />
			<Button onClick={handleUpload}>Upload</Button>
		</div>
	)
}

