import { PrismaClient } from '@prisma/client'

const prismaClientSingleton = () => {
	return new PrismaClient()
}

declare const globalThis: {
	prismaGlobal: ReturnType<typeof prismaClientSingleton>
} & typeof global

const prisma = globalThis.prismaGlobal ?? prismaClientSingleton()

export default prisma

if (process.env.NODE_ENV !== 'production') globalThis.prismaGlobal = prisma

datasource db {
  provider = "postgresql"
  url      = env("DATABASE_URL")
}

generator client {
  provider = "prisma-client-js"
}

import { readFileSync, writeFileSync } from 'fs'
import { join } from 'path'

export function main() {
	try {
		// Read the current package.json
		const packageJsonPath = join(process.cwd(), 'package.json')
		const packageJson = JSON.parse(readFileSync(packageJsonPath, 'utf-8'))

		// Add or update the prisma configuration
		packageJson.prisma = { schema: './prisma/schema' }

		// Add or update the postinstall script
		packageJson.scripts = {
			...packageJson.scripts,
			postinstall: 'npx prisma generate',
		}

		// Write the updated package.json back to the file
		writeFileSync(packageJsonPath, JSON.stringify(packageJson, null, 2))

		console.log('Successfully added Prisma configuration and postinstall script to package.json')
	} catch (error) {
		console.error('Error updating package.json:', error)
		process.exit(1)
	}
}

main()

import { env } from '@/lib/env'
import { s3 } from '@/lib/s3'
import { CreateBucketCommand, PutBucketCorsCommand, PutBucketPolicyCommand, PutPublicAccessBlockCommand } from '@aws-sdk/client-s3'

export async function main() {
	const publicBucket = env.AWS_PUBLIC_BUCKET_NAME
	const privateBucket = env.AWS_PRIVATE_BUCKET_NAME

	// await s3.send(new DeleteBucketCommand({ Bucket: publicBucket }))
	// await s3.send(new DeleteBucketCommand({ Bucket: privateBucket }))

	await s3.send(new CreateBucketCommand({ Bucket: publicBucket }))
	await s3.send(new CreateBucketCommand({ Bucket: privateBucket }))

	await s3.send(
		new PutPublicAccessBlockCommand({
			Bucket: publicBucket,
			PublicAccessBlockConfiguration: {
				BlockPublicAcls: false,
				IgnorePublicAcls: false,
				BlockPublicPolicy: false,
				RestrictPublicBuckets: false,
			},
		})
	)

	await s3.send(
		new PutBucketPolicyCommand({
			Bucket: publicBucket,
			Policy: JSON.stringify({
				Version: '2012-10-17',
				Statement: [
					{
						Sid: 'PublicReadAvatars',
						Effect: 'Allow',
						Principal: '*',
						Action: 's3:GetObject',
						Resource: `arn:aws:s3:::${publicBucket}/*`,
					},
				],
			}),
		})
	)

	await s3.send(
		new PutBucketCorsCommand({
			Bucket: privateBucket,
			CORSConfiguration: {
				CORSRules: [
					{
						AllowedOrigins: [
							// TODO: Add production url
							env.NEXT_PUBLIC_SITE_URL,
						],
						AllowedMethods: ['GET', 'PUT', 'POST'],
						AllowedHeaders: ['*'],
						ExposeHeaders: ['ETag'],
						MaxAgeSeconds: 3000,
					},
				],
			},
		})
	)
}

main()
