'use server'

import { handleFormAction } from '@/components/ui/base/action'
import { signIn, signOut } from '@/lib/auth'
import prisma from '@/lib/db'
import { getSuccessRedirect } from '@/lib/utils'
import { loginSchema, oauthSchema, registerSchema, verifyEmailSchema } from '@/validators/auth'
import { Prisma } from '@prisma/client'
import bcrypt from 'bcryptjs'
import { AuthError } from 'next-auth'
import { OAuthProviderId } from 'next-auth/providers'
import { redirect } from 'next/navigation'

export const login = async (_: unknown, formData: FormData) =>
	handleFormAction(formData, loginSchema, async (data) => {
		try {
			await signIn('credentials', { email: data.email, password: data.password, redirectTo: data.callbackUrl })
		} catch (error) {
			if (error instanceof AuthError) return { globalError: error.message }
			throw error
		}

		redirect('/dashboard')
	})

export async function logout() {
	await signOut({ redirectTo: '/login' })
}

export const oauth = async (provider: OAuthProviderId, _: unknown, formData: FormData) =>
	handleFormAction(formData, oauthSchema, async (data) => {
		try {
			await signIn(provider, { redirectTo: data.callbackUrl })
		} catch (error) {
			if (error instanceof AuthError) return { globalError: error.message }
			throw error
		}

		redirect('/dashboard')
	})

export const register = async (_: unknown, formData: FormData) =>
	handleFormAction(formData, registerSchema, async (data) => {
		try {
			const passwordHash = await bcrypt.hash(data.password, 10)
			await prisma.user.create({ data: { name: `${data.firstName} ${data.lastName}`, email: data.email, passwordHash } })
		} catch (error) {
			if (error instanceof Prisma.PrismaClientKnownRequestError && error.code === 'P2002')
				return { fieldErrors: { email: ['User already exists with that email'] } }
			else if (error instanceof AuthError) return { globalError: error.message }
			throw error
		}

		redirect(getSuccessRedirect('/login', 'Account created, please login'))
	})

export const verifyEmail = async (_prevState: unknown, formData: FormData) =>
	handleFormAction(formData, verifyEmailSchema, async (data) => {
		try {
			await signIn('resend', { email: data.email, redirect: false })
		} catch (error) {
			if (error instanceof AuthError) return { globalError: error.message }
			throw error
		}

		redirect(getSuccessRedirect('/login', 'A sign in link has been sent to your email address.'))
	})

'use server'

import { auth } from '@/lib/auth'
import { calculateTrialEndUnixTimestamp, CheckoutResponse, createOrRetrieveCustomer, stripe } from '@/lib/stripe'
import { getErrorRedirect, getSuccessRedirect, getURL } from '@/lib/utils'
import { Price } from '@prisma/client'
import Stripe from 'stripe'

export async function checkoutWithStripe(price: Price): Promise<CheckoutResponse> {
	try {
		const user = await auth()

		// Retrieve or create the customer in Stripe
		let customer: string
		try {
			customer = (await createOrRetrieveCustomer({ uuid: user.id ?? '', email: user.email ?? '' }))!
		} catch (err) {
			console.error(err)
			throw new Error('Unable to access customer record.')
		}

		let params: Stripe.Checkout.SessionCreateParams = {
			allow_promotion_codes: true,
			billing_address_collection: 'required',
			customer,
			customer_update: {
				address: 'auto',
			},
			line_items: [
				{
					price: price.id,
					quantity: 1,
				},
			],
			cancel_url: getURL('/plans'),
			success_url: getURL(getSuccessRedirect('/plans', 'Purchase successful.')),
		}

		params = {
			...params,
			mode: 'subscription',
			subscription_data: {
				trial_end: calculateTrialEndUnixTimestamp(price.trial_period_days),
			},
		}

		// Create a checkout session in Stripe
		let session
		try {
			session = await stripe.checkout.sessions.create(params)
		} catch (err) {
			console.error(err)
			throw new Error('Unable to create checkout session.')
		}

		// Instead of returning a Response, just return the data or error.
		if (session) return { sessionId: session.id }
		else throw new Error('Unable to create checkout session.')
	} catch (error) {
		if (error instanceof Error)
			return {
				errorRedirect: getErrorRedirect('/plans', error.message),
			}
		else
			return {
				errorRedirect: getErrorRedirect('/plans', 'An unknown error occurred.'),
			}
	}
}

export async function createStripePortal(currentPath: string) {
	try {
		const user = await auth()

		let customer
		try {
			customer = await createOrRetrieveCustomer({ uuid: user.id ?? '', email: user.email ?? '' })
		} catch (err) {
			console.error(err)
			throw new Error('Unable to access customer record.')
		}

		if (!customer) throw new Error('Could not get customer.')

		try {
			const { url } = await stripe.billingPortal.sessions.create({
				customer,
				return_url: getURL('/plans'),
			})
			if (!url) throw new Error('Could not create billing portal')

			return url
		} catch (err) {
			console.error(err)
			throw new Error('Could not create billing portal')
		}
	} catch (error) {
		if (error instanceof Error) {
			console.error(error)
			return getErrorRedirect(currentPath, error.message)
		} else return getErrorRedirect(currentPath, 'An unknown error occurred.')
	}
}

'use server'

import { handleFormAction } from '@/components/ui/base/action'
import { auth } from '@/lib/auth'
import prisma from '@/lib/db'
import { s3 } from '@/lib/s3'
import { getSuccessRedirect } from '@/lib/utils'
import { updateAvatarSchema, updateUserSchema } from '@/validators/user'
import { PutObjectCommand } from '@aws-sdk/client-s3'
import { getSignedUrl } from '@aws-sdk/s3-request-presigner'
import { Account } from '@prisma/client'
import bcrypt from 'bcryptjs'
import { revalidatePath } from 'next/cache'
import { redirect } from 'next/navigation'
import { env } from 'process'
import { v4 as uuidv4 } from 'uuid'

export const updateAvatar = async (_: unknown, formData: FormData) =>
	handleFormAction(formData, updateAvatarSchema, async ({ file }) => {
		const user = await auth()
		const Key = `avatars/${user.id}/${uuidv4()}`

		const cmd = new PutObjectCommand({ Bucket: env.AWS_PUBLIC_BUCKET_NAME, Key, ContentType: file.type ?? 'application/octet-stream' })
		const url = await getSignedUrl(s3, cmd, { expiresIn: 60 })

		await prisma.user
			.update({
				where: { id: user.id },
				data: { image: `https://${env.AWS_PUBLIC_BUCKET_NAME}.s3.${env.AWS_REGION}.amazonaws.com/${Key}` },
			})
			.then(async () => await fetch(url, { method: 'PUT', body: file }))

		revalidatePath('/settings')
	})

export const updateUser = async (_: unknown, formData: FormData) =>
	handleFormAction(formData, updateUserSchema, async ({ password, confirmPassword, ...data }) => {
		const user = await auth()

		await prisma.user.update({ where: { id: user.id }, data })

		if (password && confirmPassword) {
			const passwordHash = await bcrypt.hash(password, 10)
			await prisma.user.update({ where: { id: user.id }, data: { passwordHash } })
		}

		redirect(getSuccessRedirect('/settings', 'Account updated'))
	})

export async function unlinkAccount(provider: Account['provider'], providerAccountId: Account['providerAccountId']) {
	const user = await auth()
	await prisma.account.delete({ where: { provider_providerAccountId: { provider, providerAccountId }, userId: user.id } })
	redirect(getSuccessRedirect('/settings', 'Account unlinked'))
}

