import { env } from '@/lib/env'
import {
	deletePriceRecord,
	deleteProductRecord,
	manageSubscriptionStatusChange,
	stripe,
	upsertPriceRecord,
	upsertProductRecord,
} from '@/lib/stripe'
import Stripe from 'stripe'

const relevantEvents = new Set([
	'product.created',
	'product.updated',
	'product.deleted',
	'price.created',
	'price.updated',
	'price.deleted',
	'checkout.session.completed',
	'customer.subscription.created',
	'customer.subscription.updated',
	'customer.subscription.deleted',
])

export async function POST(req: Request) {
	const body = await req.text()
	const sig = req.headers.get('stripe-signature') as string
	const webhookSecret = env.STRIPE_WEBHOOK_SECRET
	let event: Stripe.Event

	try {
		if (!sig || !webhookSecret) return new Response('Webhook secret not found.', { status: 400 })
		event = stripe.webhooks.constructEvent(body, sig, webhookSecret)
		console.log(`üîî  Webhook received: ${event.type}`)
	} catch (err: unknown) {
		const error = err as Error
		console.log(`‚ùå Error message: ${error.message}`)
		return new Response(`Webhook Error: ${error.message}`, { status: 400 })
	}

	if (relevantEvents.has(event.type)) {
		try {
			switch (event.type) {
				case 'product.created':
				case 'product.updated':
					await upsertProductRecord(event.data.object as Stripe.Product)
					break
				case 'price.created':
				case 'price.updated':
					await upsertPriceRecord(event.data.object as Stripe.Price)
					break
				case 'price.deleted':
					await deletePriceRecord(event.data.object as Stripe.Price)
					break
				case 'product.deleted':
					await deleteProductRecord(event.data.object as Stripe.Product)
					break
				case 'customer.subscription.created':
				case 'customer.subscription.updated':
				case 'customer.subscription.deleted':
					const subscription = event.data.object as Stripe.Subscription
					await manageSubscriptionStatusChange(
						subscription.id,
						subscription.customer as string,
						event.type === 'customer.subscription.created'
					)
					break
				case 'checkout.session.completed':
					const checkoutSession = event.data.object as Stripe.Checkout.Session
					const subscriptionId = checkoutSession.subscription
					await manageSubscriptionStatusChange(subscriptionId as string, checkoutSession.customer as string, true)
					break
				default:
					throw new Error('Unhandled relevant event!')
			}
		} catch (error) {
			console.log(error)
			return new Response('Webhook handler failed. View your Next.js function logs.', {
				status: 400,
			})
		}
	} else {
		return new Response(`Unsupported event type: ${event.type}`, {
			status: 400,
		})
	}
	return new Response(JSON.stringify({ received: true }))
}

