'use server'

import { auth } from '@/lib/auth'
import { calculateTrialEndUnixTimestamp, CheckoutResponse, createOrRetrieveCustomer, stripe } from '@/lib/stripe'
import { getErrorRedirect, getSuccessRedirect, getURL } from '@/lib/utils'
import { Price } from '@prisma/client'
import Stripe from 'stripe'

export async function checkoutWithStripe(price: Price): Promise<CheckoutResponse> {
	try {
		const user = await auth()

		// Retrieve or create the customer in Stripe
		let customer: string
		try {
			customer = await createOrRetrieveCustomer({ uuid: user.id ?? '', email: user.email ?? '' })
		} catch (err) {
			console.error(err)
			throw new Error('Unable to access customer record.')
		}

		let params: Stripe.Checkout.SessionCreateParams = {
			allow_promotion_codes: true,
			billing_address_collection: 'required',
			customer,
			customer_update: {
				address: 'auto',
			},
			line_items: [
				{
					price: price.id,
					quantity: 1,
				},
			],
			cancel_url: getURL('/plans'),
			success_url: getURL(getSuccessRedirect('/plans', 'Purchase successful.')),
		}

		params = {
			...params,
			mode: 'subscription',
			subscription_data: {
				trial_end: calculateTrialEndUnixTimestamp(price.trial_period_days),
			},
		}

		// Create a checkout session in Stripe
		let session
		try {
			session = await stripe.checkout.sessions.create(params)
		} catch (err) {
			console.error(err)
			throw new Error('Unable to create checkout session.')
		}

		// Instead of returning a Response, just return the data or error.
		if (session) return { sessionId: session.id }
		else throw new Error('Unable to create checkout session.')
	} catch (error) {
		if (error instanceof Error)
			return {
				errorRedirect: getErrorRedirect('/plans', error.message),
			}
		else
			return {
				errorRedirect: getErrorRedirect('/plans', 'An unknown error occurred.'),
			}
	}
}

export async function createStripePortal(currentPath: string) {
	try {
		const user = await auth()

		let customer
		try {
			customer = await createOrRetrieveCustomer({ uuid: user.id ?? '', email: user.email ?? '' })
		} catch (err) {
			console.error(err)
			throw new Error('Unable to access customer record.')
		}

		if (!customer) throw new Error('Could not get customer.')

		try {
			const { url } = await stripe.billingPortal.sessions.create({
				customer,
				return_url: getURL('/plans'),
			})
			if (!url) throw new Error('Could not create billing portal')

			return url
		} catch (err) {
			console.error(err)
			throw new Error('Could not create billing portal')
		}
	} catch (error) {
		if (error instanceof Error) {
			console.error(error)
			return getErrorRedirect(currentPath, error.message)
		} else return getErrorRedirect(currentPath, 'An unknown error occurred.')
	}
}

'use client'

import { checkoutWithStripe, createStripePortal } from '@/actions/stripe'
import { ActionButton } from '@/components/ui/base/action-button'
import { env } from '@/lib/env'
import { getErrorRedirect } from '@/lib/utils'
import { Price } from '@prisma/client'
import { loadStripe } from '@stripe/stripe-js'
import { usePathname, useRouter } from 'next/navigation'
import { FormEvent, useState } from 'react'

export function SubscribeButton({ price }: { price: Price }) {
	const pathname = usePathname()
	const router = useRouter()
	const [isLoading, setIsLoading] = useState(false)

	const handleStripeCheckout = async (event: FormEvent<HTMLFormElement>) => {
		event.preventDefault()
		setIsLoading(true)

		const { errorRedirect, sessionId } = await checkoutWithStripe(price)

		if (errorRedirect) {
			setIsLoading(false)
			return router.push(errorRedirect)
		}

		if (!sessionId) {
			setIsLoading(false)
			return router.push(getErrorRedirect(pathname, 'An unknown error occurred.'))
		}

		const stripe = await loadStripe(env.NEXT_PUBLIC_STRIPE_PUBLISHABLE_KEY)
		stripe?.redirectToCheckout({ sessionId })

		setIsLoading(false)
	}

	return (
		<form onSubmit={handleStripeCheckout}>
			<ActionButton loading={isLoading}>Subscribe</ActionButton>
		</form>
	)
}

export function ManageSubscriptionButton() {
	const pathname = usePathname()
	const router = useRouter()
	const [isLoading, setIsLoading] = useState(false)

	const handleStripePortalRequest = async (event: FormEvent<HTMLFormElement>) => {
		event.preventDefault()
		setIsLoading(true)
		const redirectUrl = await createStripePortal(pathname)
		setIsLoading(false)

		return router.push(redirectUrl)
	}

	return (
		<form onSubmit={handleStripePortalRequest}>
			<ActionButton loading={isLoading} variant='outline'>
				Manage subscription
			</ActionButton>
		</form>
	)
}

import { env } from '@/lib/env'
import {
	deletePriceRecord,
	deleteProductRecord,
	manageSubscriptionStatusChange,
	stripe,
	upsertPriceRecord,
	upsertProductRecord,
} from '@/lib/stripe'
import Stripe from 'stripe'

const relevantEvents = new Set([
	'product.created',
	'product.updated',
	'product.deleted',
	'price.created',
	'price.updated',
	'price.deleted',
	'checkout.session.completed',
	'customer.subscription.created',
	'customer.subscription.updated',
	'customer.subscription.deleted',
])

export async function POST(req: Request) {
	const body = await req.text()
	const sig = req.headers.get('stripe-signature') as string
	const webhookSecret = env.STRIPE_WEBHOOK_SECRET
	let event: Stripe.Event

	try {
		if (!sig || !webhookSecret) return new Response('Webhook secret not found.', { status: 400 })
		event = stripe.webhooks.constructEvent(body, sig, webhookSecret)
		console.log(`ðŸ””  Webhook received: ${event.type}`)
	} catch (err: unknown) {
		const error = err as Error
		console.log(`âŒ Error message: ${error.message}`)
		return new Response(`Webhook Error: ${error.message}`, { status: 400 })
	}

	if (relevantEvents.has(event.type)) {
		try {
			switch (event.type) {
				case 'product.created':
				case 'product.updated':
					await upsertProductRecord(event.data.object as Stripe.Product)
					break
				case 'price.created':
				case 'price.updated':
					await upsertPriceRecord(event.data.object as Stripe.Price)
					break
				case 'price.deleted':
					await deletePriceRecord(event.data.object as Stripe.Price)
					break
				case 'product.deleted':
					await deleteProductRecord(event.data.object as Stripe.Product)
					break
				case 'customer.subscription.created':
				case 'customer.subscription.updated':
				case 'customer.subscription.deleted':
					const subscription = event.data.object as Stripe.Subscription
					await manageSubscriptionStatusChange(
						subscription.id,
						subscription.customer as string,
						event.type === 'customer.subscription.created'
					)
					break
				case 'checkout.session.completed':
					const checkoutSession = event.data.object as Stripe.Checkout.Session
					const subscriptionId = checkoutSession.subscription
					await manageSubscriptionStatusChange(subscriptionId as string, checkoutSession.customer as string, true)
					break
				default:
					throw new Error('Unhandled relevant event!')
			}
		} catch (error) {
			console.log(error)
			return new Response('Webhook handler failed. View your Next.js function logs.', {
				status: 400,
			})
		}
	} else {
		return new Response(`Unsupported event type: ${event.type}`, {
			status: 400,
		})
	}
	return new Response(JSON.stringify({ received: true }))
}

import { loginSchema } from '@/validators/auth'
import { PrismaAdapter } from '@auth/prisma-adapter'
import bcrypt from 'bcryptjs'
import NextAuth, { NextAuthConfig } from 'next-auth'
import Credentials from 'next-auth/providers/credentials'
import Github from 'next-auth/providers/github'
import Google from 'next-auth/providers/google'
import Resend from 'next-auth/providers/resend'
import prisma from './db'
import { env } from './env'
import { AuthUser } from './utils'

export const authConfig = {
	adapter: PrismaAdapter(prisma),
	callbacks: {
		authorized: ({ auth, request: { nextUrl } }) => {
			const unprotectedRoutes = ['/', '/login', '/register', '/forgot', '/privacy', '/terms']

			const isAuthed = !!auth?.user
			const isAuthRoute = unprotectedRoutes.includes(nextUrl.pathname)

			if (!isAuthRoute) {
				if (isAuthed) return true
				return false
			} else if (isAuthed) return Response.redirect(new URL('/dashboard', nextUrl))
			return true
		},
	},
	pages: { signIn: '/login' },
	providers: [
		Credentials({
			credentials: { email: {}, password: {} },
			authorize: async (credentials) => {
				const { email, password } = await loginSchema.parseAsync(credentials)
				const user = await prisma.user.findUnique({ where: { email } })

				if (!user) throw new Error('User not found')
				else if (!user.passwordHash) throw new Error('User does not have a password')
				else if (!(await bcrypt.compare(password, user.passwordHash))) throw new Error('Password does not match')

				return user
			},
		}),
		Github({ allowDangerousEmailAccountLinking: true }),
		Google({ allowDangerousEmailAccountLinking: true }),
		Resend({ from: env.AUTH_RESEND_EMAIL }),
	],
	session: { strategy: 'jwt' },
} satisfies NextAuthConfig

export const { handlers, auth: session, signIn, signOut } = NextAuth(authConfig)

export const auth = async (): Promise<AuthUser> => {
	const session = await NextAuth(authConfig).auth()
	if (!session?.user) throw new Error('Not authenticated.')

	const user = await prisma.user.findFirst({
		where: { email: session.user.email ?? '' },
		include: {
			subscriptions: {
				where: { status: 'active', price: { active: true, product: { active: true } } },
				include: { price: { include: { product: true } } },
			},
		},
	})
	if (!user) throw new Error('User not found')

	return user
}

import { Prisma } from '@prisma/client'
import Stripe from 'stripe'
import prisma from './db'
import { env } from './env'

export const stripe = new Stripe(env.STRIPE_SECRET_KEY, { apiVersion: '2025-03-31.basil' })

export type CheckoutResponse = {
	errorRedirect?: string
	sessionId?: string
}

const TRIAL_PERIOD_DAYS = 0

const toDateTime = (secs: number) => {
	const t = new Date(+0)
	t.setSeconds(secs)
	return t
}

export const calculateTrialEndUnixTimestamp = (trialPeriodDays: number | null | undefined) => {
	// Check if trialPeriodDays is null, undefined, or less than 2 days
	if (trialPeriodDays === null || trialPeriodDays === undefined || trialPeriodDays < 2) {
		return undefined
	}

	const currentDate = new Date() // Current date and time
	const trialEnd = new Date(currentDate.getTime() + (trialPeriodDays + 1) * 24 * 60 * 60 * 1000) // Add trial days
	return Math.floor(trialEnd.getTime() / 1000) // Convert to Unix timestamp in seconds
}

export const upsertProductRecord = async (product: Stripe.Product) => {
	const productData: Prisma.ProductCreateInput = {
		id: product.id,
		active: product.active,
		name: product.name,
		description: product.description ?? null,
		image: product.images?.[0] ?? null,
		metadata: product.metadata,
		features: product.marketing_features.filter((f) => f.name).map((f) => f.name!) ?? [],
	}

	await prisma.product.upsert({ where: { id: product.id }, create: productData, update: productData })
	console.log(`Product inserted/updated: ${product.id}`)
}

