export const deleteProductRecord = async (product: Stripe.Product) => {
	await prisma.product.delete({ where: { id: product.id } })
	console.log(`Product deleted: ${product.id}`)
}

export const deletePriceRecord = async (price: Stripe.Price) => {
	await prisma.price.delete({ where: { id: price.id } })
	console.log(`Price deleted: ${price.id}`)
}

export const createCustomerInStripe = async (uuid: string, email: string) => {
	const customerData = { metadata: { user_id: uuid }, email }
	const newCustomer = await stripe.customers.create(customerData)
	if (!newCustomer) throw new Error('Stripe customer creation failed.')
	return newCustomer.id
}

export const createOrRetrieveCustomer = async ({ email, uuid }: { email: string; uuid: string }) => {
	// Check if the customer already exists in Supabase
	const existingUser = await prisma.user.findUnique({ where: { id: uuid } })

	// Retrieve the Stripe customer ID using the Supabase customer ID, with email fallback
	let stripeCustomerId: string | undefined
	if (existingUser?.stripe_customer_id) {
		const existingStripeCustomer = await stripe.customers.retrieve(existingUser.stripe_customer_id)
		stripeCustomerId = existingStripeCustomer.id
	} else {
		// If Stripe ID is missing from Supabase, try to retrieve Stripe customer ID by email
		const stripeCustomers = await stripe.customers.list({ email: email })
		stripeCustomerId = stripeCustomers.data.length ? stripeCustomers.data[0].id : undefined
	}

	// If still no stripeCustomerId, create a new customer in Stripe
	const stripeIdToInsert = stripeCustomerId ? stripeCustomerId : await createCustomerInStripe(uuid, email)
	if (!stripeIdToInsert) throw new Error('Stripe customer creation failed.')

	if (existingUser && stripeCustomerId) {
		// If Supabase has a record but doesn't match Stripe, update Supabase record
		if (existingUser.stripe_customer_id !== stripeCustomerId) {
			await prisma.user.update({ where: { id: uuid }, data: { stripe_customer_id: stripeCustomerId } })
			console.warn(`Supabase customer record mismatched Stripe ID. Supabase record updated.`)
		}
		// If Supabase has a record and matches Stripe, return Stripe customer ID
		return stripeCustomerId
	} else {
		console.warn(`Supabase customer record was missing. A new record was created.`)
		const upsertedStripeCustomer = await prisma.user.update({ where: { id: uuid }, data: { stripe_customer_id: stripeIdToInsert } })
		return upsertedStripeCustomer.stripe_customer_id
	}
}

/**
 * Copies the billing details from the payment method to the customer object.
 */

const copyBillingDetailsToCustomer = async (uuid: string, payment_method: Stripe.PaymentMethod) => {
	//Todo: check this assertion
	const customer = payment_method.customer as string
	const { name, phone, address } = payment_method.billing_details
	if (!name || !phone || !address) return

	//@ts-ignore
	await stripe.customers.update(customer, { name, phone, address })
	await prisma.user.update({
		where: { id: uuid },
		data: {
			billing_address: { ...address } as Prisma.InputJsonValue,
			payment_method: { ...payment_method[payment_method.type] } as Prisma.InputJsonValue,
		},
	})
}

export const manageSubscriptionStatusChange = async (subscriptionId: string, customerId: string, createAction = false) => {
	// Get customer's UUID from mapping table.
	const customerData = await prisma.user.findUnique({ where: { stripe_customer_id: customerId } })
	if (!customerData) throw new Error(`Customer not found: ${customerId}`)

	const subscription = await stripe.subscriptions.retrieve(subscriptionId, { expand: ['default_payment_method'] })
	// Upsert the latest status of the subscription object.
	const subscriptionData: Prisma.SubscriptionCreateInput = {
		id: subscription.id,
		user: { connect: { id: customerData.id } },
		metadata: subscription.metadata,
		status: subscription.status,
		price: { connect: { id: subscription.items.data[0].price.id } },
		cancel_at_period_end: subscription.cancel_at_period_end,
		cancel_at: subscription.cancel_at ? toDateTime(subscription.cancel_at).toISOString() : null,
		canceled_at: subscription.canceled_at ? toDateTime(subscription.canceled_at).toISOString() : null,
		// TODO: Do these exist?
		// current_period_start: toDateTime(subscription.current_period_start).toISOString(),
		// current_period_end: toDateTime(subscription.current_period_end).toISOString(),
		created: toDateTime(subscription.created).toISOString(),
		ended_at: subscription.ended_at ? toDateTime(subscription.ended_at).toISOString() : null,
		trial_start: subscription.trial_start ? toDateTime(subscription.trial_start).toISOString() : null,
		trial_end: subscription.trial_end ? toDateTime(subscription.trial_end).toISOString() : null,
	}

	await prisma.subscription.upsert({ where: { id: subscription.id }, create: subscriptionData, update: subscriptionData })
	console.log(`Inserted/updated subscription [${subscription.id}] for user [${customerData.id}]`)

	// For a new subscription copy the billing details to the customer object.
	// NOTE: This is a costly operation and should happen at the very end.
	if (createAction && subscription.default_payment_method)
		await copyBillingDetailsToCustomer(customerData.id, subscription.default_payment_method as Stripe.PaymentMethod)
}

import { Prisma } from '@prisma/client'
import { clsx, type ClassValue } from 'clsx'
import { twMerge } from 'tailwind-merge'

export type AuthUser = Prisma.UserGetPayload<{ include: { subscriptions: { include: { price: { include: { product: true } } } } } }>

export function cn(...inputs: ClassValue[]) {
	return twMerge(clsx(inputs))
}

export const getURL = (path: string = '') => {
	let url =
		process?.env?.NEXT_PUBLIC_SITE_URL && process.env.NEXT_PUBLIC_SITE_URL.trim() !== ''
			? process.env.NEXT_PUBLIC_SITE_URL
			: process?.env?.NEXT_PUBLIC_VERCEL_URL && process.env.NEXT_PUBLIC_VERCEL_URL.trim() !== ''
			? process.env.NEXT_PUBLIC_VERCEL_URL
			: 'http://localhost:3000/'

	// Trim the URL and remove trailing slash if exists.
	url = url.replace(/\/+$/, '')
	// Make sure to include `https://` when not localhost.
	url = url.includes('http') ? url : `https://${url}`
	// Ensure path starts without a slash to avoid double slashes in the final URL.
	path = path.replace(/^\/+/, '')

	// Concatenate the URL and the path.
	return path ? `${url}/${path}` : url
}

const toastKeyMap = {
	blank: 'message',
	error: 'error',
	success: 'success',
} as const

const getToastRedirect = (path: string, type: keyof typeof toastKeyMap, message: string, arbitraryParams: string = '') => {
	const key = toastKeyMap[type]

	let redirectPath = `${path}?${key}=${encodeURIComponent(message)}`
	if (arbitraryParams) redirectPath += `&${arbitraryParams}`

	return redirectPath
}

export const getErrorRedirect = (path: string, message: string = '', arbitraryParams: string = '') =>
	getToastRedirect(path, 'error', message, arbitraryParams)

export const getSuccessRedirect = (path: string, message: string = '', arbitraryParams: string = '') =>
	getToastRedirect(path, 'success', message, arbitraryParams)

