import { PriceInterval } from '@prisma/client'
import { MetadataParam } from '@stripe/stripe-js'
import { config } from 'dotenv'
import Stripe from 'stripe'

config()
const stripe = new Stripe(process.env.STRIPE_SECRET_KEY!, { apiVersion: '2025-03-31.basil' })

const plans: {
	name: string
	description: string
	features: string[]
	metadata: MetadataParam
	prices: { interval: PriceInterval; unit_amount: number }[]
}[] = [
	{
		name: 'Basic',
		description: 'This is the basic plan',
		features: ['Thing 1', 'Thing 2', 'Thing 3'],
		metadata: { index: 1 },
		prices: [
			{ interval: 'month', unit_amount: 499 },
			{ interval: 'year', unit_amount: 4900 },
		],
	},
	{
		name: 'Pro',
		description: 'This is the pro plan',
		features: ['Thing 4', 'Thing 5', 'Thing 6'],
		metadata: { index: 2 },
		prices: [
			{ interval: 'month', unit_amount: 999 },
			{ interval: 'year', unit_amount: 9900 },
		],
	},
]

async function main() {
	for (const plan of plans) {
		const p = await stripe.products.create({
			name: plan.name,
			description: plan.description,
			marketing_features: plan.features.map((f) => ({ name: f })),
			metadata: plan.metadata,
		})

		for (const price of plan.prices) {
			await stripe.prices.create({
				product: p.id,
				currency: 'usd',
				recurring: { interval: price.interval, interval_count: 1 },
				unit_amount: price.unit_amount,
			})
		}
	}
}

main()
